import './App.css';
import io from "socket.io-client"
import {useEffect, useState} from 'react'
const socket = io.connect("http://localhost:3001")

function App() {
  const MAX_SIZE = 6;
  const [message, setMessage] = useState("");
  const [messageRecv, setMessageRecv] = useState("");

  function sendMessage()
  {
    socket.emit("SEND_MESSAGE", { message });
  }

  function updateScreen()
  {
    /* 
    Ok. Your program receives data from other users.
    This function needs to access some UL or OL on the DOM and simply add
    a new LI element that contains the messageRecv !
    */
    const LIST = document.getElementById("messageListId");
    LIST.appendChild(document.createElement("LI").innerHTML=messageRecv);
  }

  useEffect(() => {
    socket.on("RECEIVE_MESSAGE", (data) => {
      setMessageRecv(data.message);
      updateScreen();
    })
  }, [socket])


  return (
    <div className="App">

      <ul className="messageListClass" id="messageListId"></ul>

      <div className="messageBar">
        <input className="messageInputBar" placeholder="Message..." onChange={ (event) => {
          setMessage(event.target.value);
        }}></input>
        <button className="sendButton" onClick={sendMessage}>Send</button>
      </div>

    </div>
  );
}

export default App;
